package mapa;

public class tablero {
	int x;	//tamao del mapa
	int y;
	
	public tablero() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public tablero(int x, int y) {
		super();
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
	}

	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}
	
}
